import React, { useState } from "react";
import "./Profile.css";
import profile from "/Users/shankaralemagar/Desktop/CashCalc/frontend/src/assets/images/Profile_Page_icon/Male User.png";
import image1 from "/Users/shankaralemagar/Desktop/CashCalc/frontend/src/assets/images/Profile_Page_icon/photo.png";
import edit from "/Users/shankaralemagar/Desktop/CashCalc/frontend/src/assets/images/Profile_Page_icon/Edit.png";

const Profile = () => {
  const [showImageForm, setShowImageForm] = useState(false);
  const [showEditUsernameForm, setShowEditUsernameForm] = useState(false);
  const [showEditAccountInfoForm, setShowEditAccountInfoForm] = useState(false);

  const handleImageEditClick = () => {
    setShowImageForm(true);
    setShowEditUsernameForm(false);
    setShowEditAccountInfoForm(false);
  };

  const handleEditUsernameClick = () => {
    setShowImageForm(false);
    setShowEditUsernameForm(true);
    setShowEditAccountInfoForm(false);
  };

  const handleEditAccountInfoClick = () => {
    setShowImageForm(false);
    setShowEditUsernameForm(false);
    setShowEditAccountInfoForm(true);
  };

  return (
    <div>
      <h2>Profile</h2>
      <div className="ContentPart">
        <div className="Personal_info">
          <div className="image-container">
            <img src={profile} className="image1" alt="Profile"></img>
            <img
              src={image1}
              className="image2"
              alt="Image"
              onClick={handleImageEditClick}
            ></img>
          </div>
          <div className="detail">
            Username
            <img
              className="edit"
              src={edit}
              width="24px"
              height="24px"
              onClick={handleEditUsernameClick}
              alt="Edit Username"
            ></img>
          </div>
        </div>

        <div className="account_information">
          <div className="headPart">
            Account Information
            <img
              src={edit}
              width="24px"
              height="24px"
              onClick={handleEditAccountInfoClick}
              alt="Edit Account Information"
            ></img>
          </div>
          <div className="info">
            {showEditAccountInfoForm && (
              <>
                <div className="items">Email</div>
                <div className="items">abc@gmail.com</div>
                <div className="items">Password</div>
                <div className="items">********</div>
              </>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
